class Solution:
    result = []
    def checkInclusion(self, s1, s2):
        """
        :type s1: str
        :type s2: str
        :rtype: bool
        """
        first = {}
        length = len(s1)
        second = {}
        for i in s1:
            if i not in first.keys():
                first[i] = 1
            else:
                first[i] += 1
        sorted(first)

        for i in range(0,len(s2)-length+1):
            second.clear()
            for j in s2[i:i+length]:
                if j not in second.keys():
                    second[j] = 1
                else:
                    second[j] += 1
            sorted(second)
            if first == second:
                return True
        return False


if __name__ == '__main__':
    s = Solution()
    s1 = ""

    s2 = ""

    print(s.checkInclusion(s1,s2))